func OnEnter(ctx map[string]any) {
    narrate := ctx["narrate"].(func(string))
    broadcast := ctx["broadcast"].(func(string))
    player, _ := ctx["player"].(string)
    if player == "" {
        player = "traveler"
    }
    narrate("Lumen Clay thrums as " + player + " arrives, weaving their stride into the city's rhythm.")
    broadcast("Ambient harmonics pulse through the plaza, synchronizing to " + player + "'s heartbeat.")
}

