func OnHear(ctx map[string]any) {
    tell := ctx["tell"].(func(string))
    message, _ := ctx["message"].(string)
    lower := strings.ToLower(message)
    switch {
    case strings.Contains(lower, "guide me"):
        tell("Ascend the overlook, follow the aurora balustrade north, then ride the resonance lift when the chimes crescendo.")
    case strings.Contains(lower, "market"):
        tell("Slip through the western arcade, follow the spice banners, and let the windchimes choose the stall that needs you most.")
    case strings.Contains(lower, "library"):
        tell("Trace the glyph-lit stair to the north, pause beneath the silent bells, and the stacks will open like a fan.")
    case strings.Contains(lower, "underworks"):
        tell("Seek the sluice beneath the kiln balconies; the underworks answer to steady footsteps and a whispered passphrase.")
    case strings.Contains(lower, "observatory"):
        tell("Ride the skyrail from the overlook when the aurora pulses indigo; the stars will wait.")
    }
}
